import React, { useState } from 'react';
import { Container, Text, Button, TextInput, FileInput, Select } from '@mantine/core';
import { notifications } from '@mantine/notifications';

function App() {
  const [step, setStep] = useState(1);
  const [character, setCharacter] = useState({
    description: '',
    image: null,
    name: '',
    gender: '',
    age: '',
    voice: ''
  });

  const renderStep = () => {
    switch(step) {
      case 1:
        return <TextInput label="Describe your character" placeholder="Description" value={character.description} onChange={(val) => setCharacter({...character, description: val})} />;
      case 2:
        // Use ImageInput or appropriate input component for image
        return <FileInput label="Upload image" />;
      case 3:
        return <TextInput label="Name" placeholder="Name" value={character.name} onChange={(val) => setCharacter({...character, name: val})} />;
      case 4:
        // Assuming you have a predefined list of genders
        const genders = ['Male', 'Female', 'Other'];
        return <Select label="Gender" data={genders} value={character.gender} onChange={(val) => setCharacter({...character, gender: val})} />;
      case 5:
        return <TextInput label="Age" type="number" placeholder="Age" value={character.age} onChange={(val) => setCharacter({...character, age: val})} />;
      case 6:
        // Example voices for the dropdown
        const voices = ['Voice 1', 'Voice 2', 'Voice 3'];
        return <Select label="Select Voice" data={voices} value={character.voice} onChange={(val) => setCharacter({...character, voice: val})} />;
      default:
        return null;
    }
  };

  return (
    <Container size={400} style={{ marginTop: 50 }}>
      <Text align="center" size="xl">
        Step {step}: Create your character
      </Text>
      {renderStep()}
      <Button style={{ marginTop: 20 }} onClick={() => setStep(step - 1)} disabled={step === 1}>
        Previous
      </Button>
      <Button
        style={{ marginLeft: 10 }}
        onClick={() => {
          if(step < 6) {
            setStep(step + 1);
          } else {
            // Submit form or perform next action
          }
        }}
      >
        {step < 6 ? 'Next' : 'Submit'}
      </Button>
    </Container>
  );
}

export default App;
