import React, { useState } from 'react';
import { Textarea, Button } from '@mantine/core';

function CharacterDescription({ value, onUpdate }) {
  const [localDescription, setLocalDescription] = useState(value);
  const [error, setError] = useState('');

  const handleDescriptionChange = (event) => {
    setLocalDescription(event.target.value);
  };

  const handleSubmit = () => {
    if (localDescription.trim().length < 10) {
      setError('Description should be at least 10 characters.');
    } else {
      setError('');
      onUpdate(localDescription);
    }
  };

  return (
    <div>
      <Textarea 
        label="Describe your character" 
        placeholder="Description" 
        value={localDescription} 
        onChange={handleDescriptionChange} 
        minRows={4}
      />
      {error && <p style={{color: 'red'}}>{error}</p>}
      <Button onClick={handleSubmit}>Submit</Button>
    </div>
  );
}

export default CharacterDescription;
