import React, { useState } from 'react';
import { Textarea } from '@mantine/core';

function CharacterDescription({ value, onUpdate }) {
  const [localDescription, setLocalDescription] = useState(value);
  const [error, setError] = useState('');

  const handleSubmit = () => {
    if (localDescription.trim().length < 10) {
      setError('Description should be at least 10 characters.');
    } else {
      setError('');
      onUpdate(localDescription); // Notify the parent of the change
    }
  };

  return (
    <div>
      <Textarea 
        label="Describe your character" 
        placeholder="Description" 
        value={localDescription} 
        onChange={(e) => setLocalDescription(e.target.value)} 
        minRows={4}
      />
      {error && <p style={{color: 'red'}}>{error}</p>}
    </div>
  );
}

export default CharacterDescription;
