import React, { useState } from 'react';
import { Container, Text, Button, Textarea, TextInput, FileInput, Select } from '@mantine/core';
import { Dropzone } from '@mantine/dropzone';
import { notifications } from '@mantine/notifications';

function App() {
  const [step, setStep] = useState(1);
  const [character, setCharacter] = useState({
    description: '',
    image: null,
    name: '',
    gender: '',
    age: '',
    voice: ''
  });

  const onDrop = useCallback(acceptedFiles => {
    if (acceptedFiles.length) {
      const file = acceptedFiles[0];
      setCharacter({ ...character, image: file });
    }
  }, [character]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: 'image/*',
    maxFiles: 1,
    multiple: false
  });

  const renderStep = () => {
    switch(step) {
      case 1:
        return (
          <Textarea 
            label="Describe your character" 
            placeholder="Description" 
            value={character.description} 
            onChange={(event) => setCharacter({...character, description: event.target.value})} 
            minRows={4} // To ensure it's more like a paragraph input
          />
        );
        case 2:
          return (
            <div {...getRootProps()} style={{border: '2px dashed gray', padding: '20px', textAlign: 'center'}}>
              <input {...getInputProps()} />
              {
                isDragActive ?
                  <p>Drop the image here...</p> :
                  <p>Drag 'n' drop your character image here, or click to select one</p>
              }
              {character.image && <Text>{character.image.name}</Text>}
            </div>
          );
      case 3:
        return (
          <TextInput 
            label="Name" 
            placeholder="Name" 
            value={character.name} 
            onChange={(event) => setCharacter({...character, name: event.target.value})}
          />
        );
      case 4:
        const genders = ['Male', 'Female', 'Other'];
        return (
          <Select 
            label="Gender" 
            data={genders} 
            value={character.gender} 
            onChange={(val) => setCharacter({...character, gender: val})}
          />
        );
      case 5:
        return (
          <TextInput 
            label="Age" 
            type="number" 
            placeholder="Age" 
            value={character.age.toString()} // Convert number to string
            onChange={(event) => setCharacter({...character, age: event.target.value})}
          />
        );
      case 6:
        const voices = ['Voice 1', 'Voice 2', 'Voice 3'];
        return (
          <Select 
            label="Select Voice" 
            data={voices} 
            value={character.voice} 
            onChange={(val) => setCharacter({...character, voice: val})}
          />
        );
      default:
        return null;
    }
  };

  return (
    <Container size={400} style={{ marginTop: 50 }}>
      <Text align="center" size="xl">
        Create your character <br/>
        Step {step}: 
      </Text>
      {renderStep()}
      <Button style={{ marginTop: 20 }} onClick={() => setStep(step - 1)} disabled={step === 1}>
        Previous
      </Button>
      <Button
        style={{ marginLeft: 10 }}
        onClick={() => {
          if(step < 6) {
            setStep(step + 1);
          } else {
            // Submit form or perform next action
          }
        }}
      >
        {step < 6 ? 'Next' : 'Submit'}
      </Button>
    </Container>
  );
}

export default App;
